  //    Если у вас старая версия Node и это не работает — см. запасной вариант ниже.
  const cleaned = raw
    .replace(/[^A-Za-zА-Яа-яЁё0-9\s]+/g, ' ')
     .replace(/\s+/g, ' ')
     .trim();
    // 3) Берём первые 7 слов
