// OUTPUT: массив элементов { response_id, tool_call_id, name, args }

const res = $json;
const out = [];

function pushCall(obj, responseId,out_index) {
  const id = responseId;
  const call_id = obj.call_id;
  const name = obj.name ;
  const rawArgs = obj.arguments;
  let args = {};
  try {
    args = typeof rawArgs === 'string' ? JSON.parse(rawArgs) : rawArgs;
  } catch (e) { args = {}; }
  out.push({ json: { response_id: id, call_id: call_id, name, args,out_index } });
}

if (Array.isArray(res.output)) {
  res.output.forEach((item, index) => {
    if (item.type === 'function_call') {
      pushCall(item, res.id, index);
    }
  });
}

// Если модель не запросила tool — вернем маркер
if (!out.length) return [{ json: { response_id: res.id, no_tool_call: true, raw: res } }];
return out;

