// Вход: webhook отправил payload в виде строки JSON
const data = JSON.parse($json.body);

// Разложим по отдельным полям
return [
  {
    json: {
      utm_source: data.utm_source || '',
      utm_medium: data.utm_medium || '',
      utm_campaign: data.utm_campaign || '',
      utm_term: data.utm_term || '',
      utm_content: data.utm_content || '',
      etext: data.etext || '',
      yclid: data.yclid || '',
      gclid: data.gclid || '',
      fbclid: data.fbclid || '',
      msclkid: data.msclkid || '',
      referrer: data.referrer || '',
      page_url: data.page_url || '',
      page_title: data.page_title || '',
      is_yandex_direct: data.is_yandex_direct || false,
      yandex_reason: data.yandex_reason || '',
      decided_source_id: data.decided_source_id || '',
      ts: data.ts || ''
    }
  }
];

// вход тот самый body, который ты уже парсишь
const data = typeof $json.body === 'string' ? JSON.parse($json.body) : $json;

// полезно захватить IP/UA для дополнительной корреляции
const headers = $json.headers || {};
const ip = headers['x-forwarded-for'] || headers['cf-connecting-ip'] || headers['x-real-ip'] || '';
const ua = headers['user-agent'] || '';

const now = Date.now();

return [{
  json: {
    status: 'pending',
    ts_site: now,                    // timestamp прихода с сайта (ms)
    yclid: data.yclid || '',
    etext: data.etext || '',
    utm_source: data.utm_source || '',
    utm_medium: data.utm_medium || '',
    utm_campaign: data.utm_campaign || '',
    referrer: data.referrer || '',
    page_url: data.page_url || '',
    ip,
    ua,
    raw_site: data                    // сохраняем всё целиком на всякий случай
  }
}];
