// === Code: normalize_and_ngrams ===
// INPUT:  HTTP Webhook body { phrase: string, threshold?: number }
// OUTPUT: { threshold: number, candidates: string[], tokens: string[] }
// PURPOSE:
//   1) Normalize the phrase (lowercase, keep letters/digits/underscore and - + #)
//   2) Remove Russian prepositions as separate words (not touching - + #)
//   3) Tokenize and build 1..3-gram unique candidates preserving order
//   4) Provide threshold (default 0.88) downstream

// Input JSON: { company_name, text, threshold? }
const body = $json;
const prepositions = [
  "в","без","до","из","к","и","на","по","о","от","перед","при",
  "через","с","у","за","над","об","под","про","для","между"
];

// Нормализуем регистр
let text = String(body.args.phrase ?? "").toLowerCase();

// Экранируем спецсимволы
const escape = s => s.replace(/[.*+?^${}()|[\]\\]/g, "\\$&");
const alts = prepositions.map(escape).join("|");

// Разрешаем буквы, цифры, подчёркивание, а также - + #
const re = new RegExp(
  `(^|[^\\p{L}\\p{N}_\\-+#])(?:${alts})(?=$|[^\\p{L}\\p{N}_\\-+#])`,
  "giu"
);

// Удаляем предлоги, сохраняя левый разделитель
text = text.replace(re, "$1");

// Чистим пробелы
text = text.replace(/\s+/g, " ").trim();
//const company = body.company_name;
const threshold = (typeof body.args.threshold === 'number') ? body.threshold : 0.88;

const tokens = (text.match(/[A-Za-zА-Яа-яЁё0-9][A-Za-zА-Яа-яЁё0-9&.\-]{0,}/g) || [])
  .map(s => s.trim())
  .filter(Boolean);

const MAX_N = 3;
const set = new Set();
for (let i = 0; i < tokens.length; i++) {
  for (let n = 1; n <= MAX_N && i + n <= tokens.length; n++) {
    const phrase = tokens.slice(i, i+n).join(' ');
    set.add(phrase);
  }
}
let candidates = Array.from(set);
// если пусто — добавляем пустую строку
if (candidates.length === 0) {
  candidates = [""];
}

return [{ json: { threshold, candidates,MAX_N } }];

// === Code: explode_candidates ===
// INPUT:  { threshold, candidates[] }
// OUTPUT: array of items: { candidate, threshold }
// PURPOSE: fan-out each n-gram into its own item for embedding + DB lookup

const { threshold, candidates } = $json;

return candidates.map(c => ({ json: { candidate: c, threshold } }));

// === Code: build_vector_literal ===
// INPUT:  HTTP response(s) from OpenAI embeddings (one per candidate)
// OUTPUT: items: { vector: "[float,...]", idx, candidate, threshold }
// PURPOSE: Convert embedding array into a Postgres vector literal string and
//          carry candidate + threshold forward for alignment

const sourceAll = $('Code: explode_candidates').all();

const out = $input.all().map((r, i) => {
  // OpenAI HTTP node may put data in r.json.body.data or r.json.data depending on version
  const emb = r.json?.body?.data?.[0]?.embedding ?? r.json?.data?.[0]?.embedding;
  if (!Array.isArray(emb) || emb.length === 0) {
    throw new Error('Embedding not found for item #' + i);
  }
  const vector = '[' + emb.map(Number).join(',') + ']';
  const candidate = sourceAll[i]?.json?.candidate;
  const threshold = sourceAll[i]?.json?.threshold;
  return { json: { vector, idx: i, candidate, threshold,company_id:$('When Executed by Another Workflow').first().json.args.company_id } };
});

return out;

// === Code: threshold_flag ===
// INPUT:  Postgres results (top-1 row per candidate)
// OUTPUT: items: { candidate, matched, similarity, pass }
// PURPOSE: Compare best similarity to threshold and flag whether it passes

const ctx = $('Code: normalize_and_ngrams').first().json; // { threshold, candidates, tokens }
const exploded = $('Code: explode_candidates').all();

const out = $input.all().map((row, i) => {
  const best = row.json || {};
  const candidate = exploded[i]?.json?.candidate;
  const similarity = typeof best.similarity === 'number' ? best.similarity : -1;
  const pass = similarity >= (ctx.threshold ?? 0.88);
  return { json: { candidate, matched: best.phrase, similarity, pass } };
});

return out;

// === Code: collect_non_matches ===
// INPUT:  { candidate, matched, similarity, pass }
// OUTPUT: single item: { non_matches: string[], meta: {...} }
// PURPOSE: Keep only candidates that did NOT pass threshold (i.e., non-matches)

const ctx = $('Code: normalize_and_ngrams').first().json; // for threshold & counts

const failed = $input.all()
  .map(i => i.json)
  .filter(j => !j.pass);

return [{
  json: {
    non_matches: failed.map(x => x.candidate),
    meta: {
      threshold: ctx.threshold,
      candidates_total: ctx.candidates.length,
      checked_ngrams: '1..'+ctx.MAX_N
    }
  }
}];
