// Input JSON: { company_name, text, threshold? }
const body = $json;
const text = String(body.key_word || '').toLowerCase();
const company = body.company_id;
const threshold = (typeof body.threshold === 'number') ? body.threshold : 0.88;

const tokens = (text.match(/[A-Za-zА-Яа-яЁё0-9][A-Za-zА-Яа-яЁё0-9&.\-]{0,}/g) || [])
  .map(s => s.trim())
  .filter(Boolean);

const MAX_N = 5;
const set = new Set();
for (let i = 0; i < tokens.length; i++) {
  for (let n = 1; n <= MAX_N && i + n <= tokens.length; n++) {
    const phrase = tokens.slice(i, i+n).join(' ');
    set.add(phrase);
  }
}
return [{ json: { company_id: company, threshold, candidates: Array.from(set),MAX_N:MAX_N } }];

