// Collect ok=true, dedupe by canonical, keep highest similarity and longest phrase on tie
const items = $input.all().map(i => i.json).filter(j => j.ok);
const ctx = $('Code: extract_ngrams').first().json;
const best = new Map();
for (const h of items) {
  const key =  h.matched || 'unknown';
  const prev = best.get(key);
  if (!prev || h.similarity > prev.similarity || (h.similarity === prev.similarity && (h.candidate?.length||0) > (prev.candidate?.length||0))) {
    best.set(key, h);
  }
}
return [{ json: { hits: Array.from(best.values()),
    meta: {
      threshold: ctx.threshold,
      candidates_total: ctx.candidates.length,
      checked_ngrams: '1..'+ctx.MAX_N,
      company_id:ctx.company_id
    } } }];
