// 1) Собираем и чистим массив URL'ов
let urls = [];
for (const { json } of $input.all()) {
  const v = json?.service_url;
  if (v == null) continue;
  const s = String(v).trim();
  if (s) urls.push(s);
}

// 2) Превращаем каждый URL в отдельный выходной item
// при необходимости можно добавить index
return urls.map((url, index) => ({
  json: {
    service_url: url,
    index,          // можно удалить, если не нужен
  },
}));

