function normalizeText(text) {
  if (text == null) return text;
  let s = String(text);

  // --- Dashes → ASCII hyphen-minus (-)
  s = s
    .replace(/\u2014/g, "-") // em dash
    .replace(/\u2013/g, "-") // en dash
    .replace(/\u2012/g, "-") // figure dash
    .replace(/\u2015/g, "-") // horizontal bar
    .replace(/\u2212/g, "-"); // minus sign

  // --- Special spaces → regular space
  s = s
    .replace(/\u00A0/g, " ") // no-break space
    .replace(/\u2009/g, " ") // thin space
    .replace(/\u200A/g, " ") // hair space
    .replace(/\u2007/g, " ") // figure space
    .replace(/\u2008/g, " "); // punctuation space

  // --- Collapse any whitespace runs to a single space
  s = s.replace(/\s+/g, " ");
  s = s.replace(/[^0-9A-Za-zА-Яа-яЁё.,;:!?'"«»()[\]{}\u2026\u2014\u2013\-#\s]+/g, '');
  return s;
}
