let incident_text = '*Анализ дневных отчетов завершен\n* ';
let client_name = $input.first().json.company_name;
// Экранирование под Telegram MarkdownV2
function escapeMarkdownV2(s) {
  // список из оф. доки Telegram для MarkdownV2:
  // _ * [ ] ( ) ~ ` > # + - = | { } . !
  return String(s).replace(/([_*\[\]()~`>#+\-=|{}.!])/g, '\\$1');
}
// Универсальный форматтер: "до часа — X мин", "больше часа — H ч M мин"
function formatDurationToHM(input) {
  const ms = toMilliseconds(input);
  const totalMinutes = Math.round(ms / 60000);

  if (totalMinutes < 60) {
    return `${totalMinutes} мин`;
  } else {
    const h = Math.floor(totalMinutes / 60);
    const m = totalMinutes % 60;
    return m ? `${h} ч ${m} мин` : `${h} ч`;
  }
}

// ---- Парсеры ----
function toMilliseconds(v) {
  if (v == null) throw new Error('No duration provided');

  // 1) Объект со свойствами
  if (typeof v === 'object' && !Array.isArray(v)) {
    const h = num(v.hours);
    const m = num(v.minutes);
    const s = num(v.seconds);
    const ms = num(v.milliseconds);
    return ((h*3600 + m*60 + s) * 1000) + ms;
  }

  // 2) Число: миллисекунды (если маленькое целое — трактуем как секунды)
  if (typeof v === 'number') {
    if (Number.isInteger(v) && Math.abs(v) < 1e6) return v * 1000; // секундные значения
    return v; // миллисекунды
  }

  // 3) Строки
  if (typeof v === 'string') {
    const s = v.trim();

    // 3.1 ISO 8601: PT#H#M#S
    const iso = /^P(T(?:(\d+)H)?(?:(\d+)M)?(?:(\d+(?:\.\d+)?)S)?)$/i.exec(s);
    if (iso) {
      const h = num(iso[1]); // в этой группе вся "T...", нам нужны 2..4 — поправим:
    }
    const iso2 = /^PT(?:(\d+)H)?(?:(\d+)M)?(?:(\d+(?:\.\d+)?)S)?$/i.exec(s);
    if (iso2) {
      const h = num(iso2[1]);
      const m = num(iso2[2]);
      const sec = num(iso2[3]);
      return ((h*3600 + m*60 + sec) * 1000);
    }

    // 3.2 HH:MM:SS(.sss) или MM:SS(.sss)
    const hms = /^(\d{1,2}):([0-5]?\d)(?::([0-5]?\d(?:\.\d+)?))?$/.exec(s);
    if (hms) {
      const a = hms[1], b = hms[2], c = hms[3];
      let h = 0, m = 0, sec = 0;
      if (c != null) { // HH:MM:SS
        h = num(a); m = num(b); sec = num(c);
      } else {         // MM:SS
        m = num(a); sec = num(b);
      }
      return ((h*3600 + m*60 + sec) * 1000);
    }

    // 3.3 Человеческие: "1h 3m 44.6s", "3m44s", "90s"
    const human = /^\s*(?:(\d+)\s*h)?\s*(?:(\d+)\s*m)?\s*(?:(\d+(?:\.\d+)?)\s*s)?\s*$/i.exec(s.replace(/,/g,'.'));
    if (human && (human[1] || human[2] || human[3])) {
      const h = num(human[1]);
      const m = num(human[2]);
      const sec = num(human[3]);
      return ((h*3600 + m*60 + sec) * 1000);
    }

    // 3.4 Чистое число в строке → сек/мс
    if (/^\d+(\.\d+)?$/.test(s)) {
      const val = Number(s);
      return (Number.isInteger(val) && val < 1e6) ? val * 1000 : val;
    }

    throw new Error('Unsupported duration format: ' + s);
  }

  throw new Error('Unsupported input type: ' + typeof v);
}

function num(x) { return x ? Number(x) : 0; }

incident_text += '*'+client_name+'*';

for (const { json: i } of $input.all()) {
  let started= Date(i.started);
  if (i.company_name === client_name) {
    incident_text +=escapeMarkdownV2('\n  Рекламная кампания: ' + i.campaign_yandex_id +' за '+ i.report_date);
  } else {
    client_name = i.company_name;
    incident_text += `\n\nКлиент: *${client_name}*`;
    incident_text +=escapeMarkdownV2('\n  Рекламная кампания: ' + i.campaign_yandex_id +' за '+ i.report_date);
  }
}

return [{ json: { text: incident_text } }];

// Парсинг ISO-дат вроде 2025-11-09T00:00:00.000Z
function parseDate(str) {
  if (!str) return null;
  const d = new Date(str);
  if (Number.isNaN(d.getTime())) return null;

  // обрезаем время до 00:00:00 по UTC
  return new Date(Date.UTC(
    d.getUTCFullYear(),
    d.getUTCMonth(),
    d.getUTCDate()
  ));
}

function formatDate(date) {
  const y = date.getUTCFullYear();
  const m = String(date.getUTCMonth() + 1).padStart(2, '0');
  const d = String(date.getUTCDate()).padStart(2, '0');
  return `${y}-${m}-${d}`;
}

// "Вчера" в UTC
const now = new Date();
const todayUtc = new Date(Date.UTC(
  now.getUTCFullYear(),
  now.getUTCMonth(),
  now.getUTCDate()
));
const yesterday = new Date(todayUtc);
yesterday.setUTCDate(yesterday.getUTCDate() - 1);

const inputItems = $input.all();
const out = [];

for (const { json } of inputItems) {
  const {
    company_id,
    campaign_yandex_id,
    start_date,
    company_name,
    max_imported,
  } = json;

  const startDateParsed   = parseDate(start_date);
  const maxImportedParsed = parseDate(max_imported);

  // Если нет валидной стартовой даты — пропускаем
  if (!startDateParsed) continue;

  // Если max_imported есть — начинаем со следующего дня,
  // иначе — с самого start_date
  let rangeStart;
  if (maxImportedParsed) {
    rangeStart = new Date(maxImportedParsed);
    rangeStart.setUTCDate(rangeStart.getUTCDate() + 1);
  } else {
    rangeStart = new Date(startDateParsed);
  }

  // Если первая дата диапазона > вчера — ничего не создаём
  if (rangeStart > yesterday) continue;

  // Генерируем по одному элементу на каждую дату
  for (let d = new Date(rangeStart); d <= yesterday; d.setUTCDate(d.getUTCDate() + 1)) {
    out.push({
      json: {
        company_id,
        campaign_yandex_id,
        report_date: formatDate(d),
        company_name,
      },
    });
  }
}

return out;
