const workflowData = $getWorkflowStaticData('global');
workflowData.my_prompt = items[0].json;
return items;

return [{
  json: 
 {
  "model": "gpt-5-2025-08-07",
  "input": [
    {
      "role": "developer",
      "content": [
        {
          "type": "input_text",
          "text": "Ты — генератор заголовков рекламных объявлений. Твоя задача по каждой пользовательской фразе создать один короткий заголовок, строго соблюдая правила."
        }
      ]
    },
    {
      "role": "user",
      "content": [
        {
          "type": "input_text",
          "text": $json.my_prompt
        }
      ]
    }
  ],
  "text": {
    "format": {
      "type": "json_schema",
      "name": "headers",
      "strict": true,
      "schema": {
        "type": "object",
        "properties": {
          "headers": {
            "type": "array",
            "description": "List of header objects.",
            "items": {
              "type": "object",
              "properties": {
                "header": {
                  "type": "string",
                  "description": "A string up to 56 characters.",
                  "minLength": 0,
                  "maxLength": 56
                },
                "id": {
                  "type": "string",
                  "description": "Unique identifier for the header object.",
                  "minLength": 1
                }
              },
              "required": [
                "header",
                "id"
              ],
              "additionalProperties": false
            },
            "minItems": 0
          }
        },
        "required": [
          "headers"
        ],
        "additionalProperties": false
      }
    },
    "verbosity": "low"
  },
  "reasoning": {
    "effort": "low",
    "summary": "detailed"
  },
  "tools": [],
  "store": false,
  "include": [
    "reasoning.encrypted_content",
    "web_search_call.action.sources"
  ]
}

}];

const workflowData = $getWorkflowStaticData('global');
let my_prompt = workflowData.my_prompt.data;

for (const item of $input.all()) {
  item.json.my_prompt = my_prompt.replaceAll("{{company_description}}", workflowData.company_description).replaceAll("{{length}}", $('When Executed by Another Workflow').first().json.length);
}

return $input.all();

