}
function safeParseJSON(x) {
  if (x == null) return null;
  if (typeof x === 'object') return x;
  const s = stripCodeFences(String(x));
  try { return JSON.parse(s); } catch {
    const m = s.match(/{[\s\S]*}/);
    if (m) { try { return JSON.parse(m[0]); } catch {} }
