function stripCodeFences(s) {
  if (typeof s !== 'string') return s;
  let t = s.trim();
  if (t.startsWith('```')) t = t.replace(/^```[a-zA-Z]*\n?/, '').replace(/```$/, '').trim();
  return t;
}
function parseJson(s) {
  const t = stripCodeFences(String(s ?? ''));
  try { return JSON.parse(t); } catch { return null; }
}

