// ---------- helpers ----------
function tryGetItems(nodeName) {
  try { const arr = $items(nodeName, 0, 0); return Array.isArray(arr) ? arr : []; }
  catch { return []; }
}
// универсальный парсер времени: число мс, строка с цифрами, ISO
function toMs(v) {
  if (v == null) return 0;
  if (typeof v === 'number' && Number.isFinite(v)) return v;
  if (typeof v === 'string') {
    const s = v.trim();
    if (/^\d+$/.test(s)) {              // "1728391023456"
      const n = parseInt(s, 10);
      return Number.isFinite(n) ? n : 0;
    }
    const t = Date.parse(s);            // "2025-10-08T00:27:00.123Z"
    return Number.isFinite(t) ? t : 0;
  }
  return 0;
}
function rowTs(row) {
  // приоритет: ts_site (мс), иначе createdAt (ISO из Data Table)
  return toMs(row.ts_site) || toMs(row.createdAt);
}
function iso(ms) { try { return new Date(ms).toISOString(); } catch { return null; } }

// ---------- CRM payload ----------
let crm;
const wItems = tryGetItems('Webhook');  // переименуй, если нода называется иначе
if (wItems.length) {
  const w = wItems[0].json || {};
  crm = typeof w.body === 'string' ? JSON.parse(w.body) : w;
} else {
  crm = typeof $json.body === 'string' ? JSON.parse($json.body) : $json;
}

// ---------- pending строки из Data Table ----------
let rows = tryGetItems('Get visitors').map(i => i.json); // переименуй, если нужно
if (!rows.length) rows = $input.all().map(i => i.json);

// ---------- окно и выбор ----------
const now = Date.now();
const WINDOW_MS = 10 * 60 * 1000; // ±10 минут (можно временно увеличить до 30 мин для диагностики)

const decorated = rows.map(r => {
  const t = rowTs(r);
  return {
    id: r.id,
    ts_num: t,
    ts_readable: iso(t),
    createdAt: r.createdAt || null,
    ts_site_raw: r.ts_site ?? null,
    delta_ms: Math.abs(t - now),
    page_url: r.page_url || null,
    pending: r.pending
  };
}).filter(d => d.ts_num > 0)
  .sort((a, b) => a.delta_ms - b.delta_ms);

const inWindow = decorated.filter(d => d.delta_ms <= WINDOW_MS);
const best = inWindow[0] || null;

// ---------- результат ----------
if (best) {
  const siteRow = rows.find(r => r.id === best.id) || {};
  return [{
    json: {
      matched: true,
      id: best.id,
      ts_site: best.ts_num,
      delta_ms: best.delta_ms,
      site: siteRow,
      crm,
      debug: {
        now_iso: iso(now),
        window_ms: WINDOW_MS,
        candidates_total: rows.length,
        in_window: inWindow.length,
        top3: decorated.slice(0,3)   // ближайшие 3 для проверки
      }
    }
  }];
} else {
  return [{
    json: {
      matched: false,
      reason: 'no_candidate_in_window',
      crm,
      debug: {
        now_iso: iso(now),
        window_ms: WINDOW_MS,
        candidates_total: rows.length,
        top5: decorated.slice(0,5)   // покажет почему не попали в окно (обычно ts_num=0)
      }
    }
  }];
}

/**
 * AlfaCRM update by entity_id (works in n8n "Code in JavaScript" node)
 * - Logs in (POST /v2api/auth/login)
 * - Optionally fetches current customer to append note
 * - Updates customer (POST /v2api/{branch}/customer/update?id=...)
 *
 * Expects $json like:
 * {
 *   "entity_id": 12345,
 *   "alfacrm": { "host":"xxx.s20.online", "email":"...", "api_key":"...", "branchId":1, "x_app_key": "optional" },
 *   "update": {
 *     "lead_status_ids":[23],
 *     "lead_source_id":8,
 *     "note_append":"Updated via n8n",
 *     "custom_value_json":{"12":"...", "13":"..."},
 *     "custom_plain":{"custom_yclid":"..."} // optional passthrough
 *   }
 * }
 */



// ---------- helpers ----------
const toNum = (v) => {
  if (v === undefined || v === null) return undefined;
  const n = Number(String(v).trim());
  return Number.isFinite(n) ? n : undefined;
};

async function http(req) {
  // n8n Code node helper
  return await this.helpers.httpRequest(req);
}

async function login(self, { host, email, api_key, x_app_key }) {
  const headers = { 'Accept': 'application/json', 'Content-Type': 'application/json' };
  if (x_app_key) headers['X-APP-KEY'] = x_app_key;

  const res = await http.call(self, {
    method: 'POST',
    url: `https://${host}/v2api/auth/login`,
    headers,
    body: { email, api_key },
    json: true,
    gzip: true,
    timeout: 15000,
  });
  if (!res?.token) throw new Error('AlfaCRM: no token in auth response');
  return res.token;
}

async function getCustomer(self, { host, token, branchId, entity_id, x_app_key }) {
  const headers = {
    'Accept': 'application/json',
    'Content-Type': 'application/json',
    'X-ALFACRM-TOKEN': token,
  };
  if (x_app_key) headers['X-APP-KEY'] = x_app_key;

  const res = await http.call(self, {
    method: 'POST',
    url: `https://${host}/v2api/${branchId}/customer/index`,
    headers,
    body: { id: entity_id, page: 0 },
    json: true,
    gzip: true,
    timeout: 15000,
  });
  return Array.isArray(res?.items) ? res.items.find(i => Number(i.id) === Number(entity_id)) : null;
}

async function updateCustomer(self, { host, token, branchId, entity_id, payload, x_app_key }) {
  const headers = {
    'Accept': 'application/json',
    'Content-Type': 'application/json',
    'X-ALFACRM-TOKEN': token,
  };
  if (x_app_key) headers['X-APP-KEY'] = x_app_key;

  return await http.call(self, {
    method: 'POST',
    url: `https://${host}/v2api/${branchId}/customer/update`,
    qs: { id: entity_id },
    headers,
    body: payload,
    json: true,
    gzip: true,
    timeout: 20000,
  });
}

function buildPayload(update, currentNote) {
  const payload = {};
  if (Array.isArray(update.lead_status_ids)) payload.lead_status_ids = update.lead_status_ids;
  if (typeof update.lead_source_id === 'number') payload.lead_source_id = update.lead_source_id;

  // custom_value_json — ВСЕГДА строкой
  if (update.custom_value_json && typeof update.custom_value_json === 'object') {
    payload.custom_value_json = JSON.stringify(update.custom_value_json);
  } else if (typeof update.custom_value_json === 'string') {
    payload.custom_value_json = update.custom_value_json;
  }

  if (update.custom_plain && typeof update.custom_plain === 'object') {
    Object.assign(payload, update.custom_plain); // custom_yclid и т.п.
  }

  if (update.note_append) {
    payload.note = (currentNote || '') + (currentNote ? '\n' : '') + String(update.note_append);
  }
  return payload;
}

// ---------- input normalization ----------
const input = $json;

if (!input?.alfacrm?.host || !input?.alfacrm?.email || !input?.alfacrm?.api_key) {
  throw new Error('Provide alfacrm.host, alfacrm.email, alfacrm.api_key');
}

const branchId =
  toNum(input.alfacrm.branchId) ??
  toNum(input.branch_id) ??
  toNum(input.body?.branch_id);

if (!branchId) throw new Error('Provide alfacrm.branchId or body.branch_id');

const entity_id =
  toNum(input.entity_id) ??
  toNum(input.customerId) ??
  toNum(input.body?.entity_id) ??
  toNum(input.params?.entity_id) ??
  toNum(input.query?.entity_id);

if (!entity_id) {
  console.log('Got entity_id candidates:', {
    entity_id: input.entity_id,
    body: input.body?.entity_id,
    params: input.params?.entity_id,
    query: input.query?.entity_id,
  });
  throw new Error('Provide numeric entity_id');
}

const { host, email, api_key, x_app_key } = input.alfacrm;
const update = input.update || {};

// ---------- flow ----------
let token = await login(this, { host, email, api_key, x_app_key });

let existing = null;
try {
  existing = await getCustomer(this, { host, token, branchId, entity_id, x_app_key });
} catch (e) {
  // not fatal
}

const payload = buildPayload(update, existing?.note);

try {
  const res = await updateCustomer(this, { host, token, branchId, entity_id, payload, x_app_key });
  return [{ json: { success: true, response: res, sent: payload, entity_id, branchId } }];
} catch (err) {
  const status = err?.statusCode || err?.response?.statusCode;
  // If token expired mid-flight (rare), try once more with fresh token
  if (status === 401) {
    token = await login(this, { host, email, api_key, x_app_key });
    const res2 = await updateCustomer(this, { host, token, branchId, entity_id, payload, x_app_key });
    return [{ json: { success: true, response: res2, sent: payload, entity_id, branchId, retried: true } }];
  }
  throw new Error(`AlfaCRM update failed: ${JSON.stringify(err?.response?.body || err)}`);
}
