const ExcelJS = require('exceljs');

/*
Запуск:
node xlsx_apply_template.js \
  /data/in/template.xlsx \
  /data/in/target.xlsx \
  /data/out/output_with_header.xlsx \
  12 1 1 \
  "СТРОКА_ДЛЯ_E8" \
  "СТРОКА_ДЛЯ_E9" \
  false

Аргументы:
 2: templatePath
 3: targetPath
 4: outPath
 5: HEADER_ROWS (по умолчанию 12 → данные начнутся со строки 13)
 6: templateSheetIndex (1-based)
 7: targetSheetIndex   (1-based)
 8: e8Value (string)
 9: e9Value (string)
10: CLEAR_BELOW ("true"/"false"; по умолчанию false)
*/

const templatePath = process.argv[2];
const targetPath   = process.argv[3];
const outPath      = process.argv[4];
const HEADER_ROWS  = parseInt(process.argv[5] || '12', 10);
const templateSheetIndex = parseInt(process.argv[6] || '1', 10);
const targetSheetIndex   = parseInt(process.argv[7] || '1', 10);
const e8Value     = process.argv[8] || '';
const e9Value     = process.argv[9] || '';
const CLEAR_BELOW = String(process.argv[10] || 'false').toLowerCase() === 'true';

(async () => {
  const wbTpl = new ExcelJS.Workbook();
  await wbTpl.xlsx.readFile(templatePath);
  const wsTpl = wbTpl.getWorksheet(templateSheetIndex);
  if (!wsTpl) throw new Error('Template worksheet not found');

  const wbSrc = new ExcelJS.Workbook();
  await wbSrc.xlsx.readFile(targetPath);
  const wsSrc = wbSrc.getWorksheet(targetSheetIndex);
  if (!wsSrc) throw new Error('Target worksheet not found');

  const startRow = HEADER_ROWS + 1;

  if (CLEAR_BELOW) {
    const toDelete = Math.max(0, (wsTpl.rowCount || 0) - HEADER_ROWS);
    if (toDelete > 0) wsTpl.spliceRows(startRow, toDelete);
  }

// стало:
const maxRow = wsSrc.rowCount || 0;

// находим самый правый непустой столбец, проходя по всем строкам
let maxCol = 0;
wsSrc.eachRow({ includeEmpty: true }, (row) => {
  // ищем последнюю непустую ячейку в строке
  // exceljs хранит массив row._cells; безопасно проверить value
  for (let i = row.cellCount; i >= 1; i--) {
    const cell = row.getCell(i);
    if (cell && cell.value !== null && cell.value !== undefined && cell.value !== '') {
      if (i > maxCol) maxCol = i;
      break;
    }
  }
});

  for (let r = 1; r <= maxRow; r++) {
    const srcRow = wsSrc.getRow(r);
    const dstRow = wsTpl.getRow(startRow + r - 1);
    for (let c = 1; c <= maxCol; c++) {
      const srcCell = srcRow.getCell(c);
      const dstCell = dstRow.getCell(c);
      dstCell.value = srcCell.value;
      if (srcCell.numFmt) dstCell.numFmt = srcCell.numFmt;
    }
    if (dstRow.commit) dstRow.commit();
  }

  // Устанавливаем E8 и E9 после вставки
  wsTpl.getCell('E8').value = e8Value;
  wsTpl.getCell('E9').value = e9Value;

  await wbTpl.xlsx.writeFile(outPath);
  process.stdout.write(outPath);
})().catch(e => { console.error(e); process.exit(1); });
EOF

