// Loop over input items and add a new field called 'myNewField' to the JSON of each one
function takeByComma(str, maxLen = 4096) {
  if (str == null) return '';
  str = String(str);

  // Если строка и так короче лимита — возвращаем как есть
  if (str.length <= maxLen) return str;

  const parts = str.split(',');
  let result = '';

  for (const part of parts) {
    const phrase = part;            // при необходимости можно .trim()
    const sep = result === '' ? '' : ',';  // запятая между фразами
    const candidate = result + sep + phrase;

    if (candidate.length > maxLen) {
      // Если даже первая фраза длиннее лимита — обрезаем её "как есть"
      if (!result) {
        return phrase.slice(0, maxLen);
      }
      break;
    }

    result = candidate;
  }

  return result;
}



for (const item of $input.all()) {
  item.json.group_minus_words = takeByComma(item.json.group_minus_words,4096);
}

return $input.all();
