const workflowData = $getWorkflowStaticData('global');
let my_prompt = workflowData.my_prompt.data;

for (const item of $input.all()) {
  item.json.my_prompt = my_prompt.replaceAll("{company_name}", $('set company name').first().json.company_name).replaceAll("{services_list}", item.json.services_list);
}

return $input.all();

// Loop over input items and add a new field called 'myNewField' to the JSON of each one
let services_list='';
for (const item of $input.all()) {
  services_list = services_list + item.json.service_name + ' ' + item.json.service_description + '. ';
}

return [
  { json: { services_list } }
];
