// Берём первый item из входных данных
const input = items[0].json;

// Достаём массив competitors
const competitors = input.arr.competitors;

// Разворачиваем каждый объект в отдельный item
return competitors.map((c, index) => ({
  json: {
    index,
    competitor_name: c.competitor_name,
    competitor_type: c.competitor_type,
    competitor_url: c.competitor_url
  }
}));
