      "type": "string",
      "maxLength": 81,
      "description": "Тело объявления (обязательное поле, не более 81 символа)"
    },
    "ad_length": {
      "type": "number",
      "description": "Числовое значение длины (обязательное поле)"
    }
  },
  "required": ["Ad_header", "Ad_body", "ad_length"],
  "additionalProperties": false
}

