// Персистентное хранилище для этой Code-ноды
const store = $getWorkflowStaticData('node');

// Сбрасываем rows один раз на запуск воркфлоу
const execId = $execution.id;        // id текущего запуска
if (store._execId !== execId) {
  store._execId = execId;
  store.rows = [];
}

const inItems = $input.all();

for (const item of inItems) {
  const out = item.json.output ?? {};
  store.rows.push({
    Ad_header: out.Ad_header,
    length: out.length,
  });
}

return inItems; // пропускаем дальше по циклу

const store = $getWorkflowStaticData('node');
const rows = store.rows ?? [];

if (!rows.length) {
  return [{ json: { info: 'Нет данных для CSV' } }];
}

// отдаём строки как items → дальше Spreadsheet File: To File (CSV)
return rows.map(r => ({ json: r }));

// persistent storage этой ноды
const data = $getWorkflowStaticData('node');

// 1) пытаемся взять length из первого входного item'а
let inputLen;
if (items && items.length > 0 && items[0].json) {
  const candidate = Number(items[0].json.length);
  if (Number.isFinite(candidate)) inputLen = candidate;
}

// 2) базовое значение: либо из входа, либо из staticData
const base = (inputLen !== undefined) ? inputLen : (Number(data.length) || 0);

// 3) инкремент
const next = base + 1;

// 4) сохраняем «вечный» счётчик между запусками
data.length = next;

// 5) возвращаем как единственный item (можно слить с входом при желании)
return [{ length: next }];
