// Максимальный размер группы
const BATCH_SIZE = 100; // можно поменять при необходимости

const inputItems = $input.all();

// 1) Собираем только нужные поля из входных элементов
const flat = inputItems.map(({ json }) => ({
  key_word: json.key_word,
  key_word_id: json.key_word_id,
}));

// 2) Разбиваем на батчи по BATCH_SIZE
const output = [];
for (let i = 0; i < flat.length; i += BATCH_SIZE) {
  const batch = flat.slice(i, i + BATCH_SIZE);

  output.push({
    json: {
      items: batch,
      batch_index: output.length,      // индекс батча (0,1,2,... при желании можно удалить)
      batch_size: batch.length,        // размер текущего батча
      total_items: flat.length,        // общее число элементов (для контроля)
    },
  });
}

return output;

