// Берём текст из указанного поля
const raw = $input.first().json?.output?.[1]?.content?.[0]?.text;

if (!raw) {
  return [];
}

let data;

// Если это строка — парсим JSON, если уже объект — используем как есть
if (typeof raw === 'string') {
  try {
    data = JSON.parse(raw);
  } catch (e) {
    throw new Error('Не удалось распарсить JSON в output[1].content[0].text: ' + e.message);
  }
} else {
  data = raw;
}

// Ожидаем, что внутри есть поле key_words_resolutions — массив объектов
const list = data?.key_words_resolutions;

if (!Array.isArray(list)) {
  return [];
}

// Формируем набор выходных items с нужными полями
return list.map(entry => ({
  json: {
    key_word_id: String(entry.key_word_id ?? ''),
    is_correct_phrase: Boolean(entry.is_correct_phrase),
  },
}));

