// Получаем текущее значение переменной из постоянного хранилища
let length = $getWorkflowStaticData('global').length || 0;

// Увеличиваем на 1
length++;

// Сохраняем обновлённое значение обратно
$getWorkflowStaticData('global').length = length;

// Возвращаем результат
return [{ length }];
