// ---------- helpers ----------
function tryGetItems(nodeName) {
  try {
    const arr = $items(nodeName, 0, 0);
    return Array.isArray(arr) ? arr : [];
  } catch (e) {
    return [];
  }
}
function toNumber(v) {
  if (v == null) return 0;
  const n = Number(v);
  if (!Number.isNaN(n) && Number.isFinite(n)) return n;
  const t = Date.parse(v);
  return Number.isFinite(t) ? t : 0;
}
function rowTs(row) {
  // приоритет своей метки, иначе — createdAt Data Table
  return toNumber(row.ts_site) || toNumber(row.createdAt);
}

// ---------- CRM payload ----------
let crm;
const webhookItems = tryGetItems('Webhook');           // <— замени имя, если у тебя нода названа иначе
if (webhookItems.length) {
  const w = webhookItems[0].json || {};
  crm = typeof w.body === 'string' ? JSON.parse(w.body) : w;
} else {
  crm = typeof $json.body === 'string' ? JSON.parse($json.body) : $json;
}

// ---------- pending из Data Table ----------
let pendingItems = tryGetItems('Get row(s)').map(i => i.json);  // <— замени имя при необходимости
if (!pendingItems.length) pendingItems = $input.all().map(i => i.json);

// ---------- параметры сопоставления ----------
const now = Date.now();
const WINDOW_MS = 10 * 60 * 1000;  // ±10 минут

// фильтр по окну времени
const inWindow = pendingItems.filter(r => Math.abs(rowTs(r) - now) <= WINDOW_MS);

// ближайшая по времени
let best = null;
if (inWindow.length) {
  best = inWindow
    .slice()
    .sort((a, b) => Math.abs(rowTs(a) - now) - Math.abs(rowTs(b) - now))[0];
}

// ---------- результат ----------
if (best) {
  const ts = rowTs(best);
  return [{
    json: {
      matched: true,
      id: best.id,                         // id строки Data Table из Select
      ts_site: ts,
      delta_ms: Math.abs(ts - now),
      site: best,
      crm: crm
    }
  }];
} else {
  return [{
    json: {
      matched: false,
      reason: 'no_candidate_in_window',
      window_ms: WINDOW_MS,
      pending_count: pendingItems.length,
      crm: crm
    }
  }];
}
