/**
 * AlfaCRM create customer (n8n Code)
 * branch_ids = [1] (Саратов)
 * Телефон выбирается умно: 1_7, 1_3, phone, tel, потом глубокий поиск — берём первый валидный.
 * Отправляем телефон и в contact_contacts, и в phone (массивы строк).
 * На практике сохраняется в contact_contacts — это ок.
 */

async function http(req) { return await this.helpers.httpRequest(req); }

async function login(self, { host, email, api_key }) {
  const res = await http.call(self, {
    method: 'POST',
    url: `https://${host}/v2api/auth/login`,
    headers: { 'Content-Type': 'application/json' },
    body: { email, api_key },
    json: true,
  });
  if (!res?.token) throw new Error('Auth failed');
  return res.token;
}

async function createCustomer(self, { host, token, payload }) {
  try {
    const res = await http.call(self, {
      method: 'POST',
      url: `https://${host}/v2api/1/customer/create`,
      headers: {
        'Content-Type': 'application/json',
        'X-ALFACRM-TOKEN': token,
      },
      body: payload,
      json: true,
      gzip: true,
      timeout: 20000,
    });
    return { success: true, response: res };
  } catch (err) {
    return {
      success: false,
      status: err?.response?.statusCode,
      error: err?.response?.body || err.message,
    };
  }
}

// ---------- helpers ----------
const notEmpty = v => v !== undefined && v !== null && String(v).trim() !== '';
const toNum = v => {
  if (!notEmpty(v)) return undefined;
  const n = Number(String(v).trim());
  return Number.isFinite(n) ? n : undefined;
};
function asObj(maybe) {
  if (!maybe) return {};
  if (typeof maybe === 'object') return maybe;
  if (typeof maybe === 'string') {
    try { return JSON.parse(maybe); } catch { return {}; }
  }
  return {};
}
function findKeyDeep(obj, keyMatcher) {
  const seen = new Set();
  const stack = [obj];
  while (stack.length) {
    const cur = stack.pop();
    if (!cur || typeof cur !== 'object') continue;
    if (seen.has(cur)) continue;
    seen.add(cur);
    if (Array.isArray(cur)) { for (const v of cur) stack.push(v); continue; }
    for (const [k, v] of Object.entries(cur)) {
      if (keyMatcher(k)) return v;
      if (v && typeof v === 'object') stack.push(v);
    }
  }
  return undefined;
}

// нормализуем телефон: оставляем + и цифры, приводим к +XXXXXXXX, базовая валидация (8..15 цифр)
function normalizePhone(raw) {
  const s = String(raw || '').trim();
  if (!s) return '';
  let keep = s.replace(/[^\d+]/g, '');

  if (keep.startsWith('00')) keep = '+' + keep.slice(2);

  const digitsOnly = keep.replace(/\D/g, '');
  if (digitsOnly.length === 11 && keep[0] === '8') keep = '+7' + digitsOnly.slice(1);
  if (keep[0] !== '+') keep = '+' + digitsOnly;

  const finalDigits = keep.replace(/\D/g, '');
  if (finalDigits.length < 8 || finalDigits.length > 15) return '';
  return keep;
}

// выбираем первый валидный номер из кандидатов; если нет — глубокий поиск
function pickPhoneSmart(input) {
  const f = asObj(input.fields);

  const candidates = [
    f['1_7'],   // у тебя бывает тут
    f['1_3'],   // и тут
    input.phone,
    input.tel,
  ].filter(notEmpty);

  for (const c of candidates) {
    const n = normalizePhone(c);
    if (n) return { phone: n, source: 'candidates', raw: c };
  }

  // глубокий поиск по строкам
  const seen = new Set(); const stack = [input];
  while (stack.length) {
    const cur = stack.pop();
    if (!cur || typeof cur !== 'object') continue;
    if (seen.has(cur)) continue; seen.add(cur);
    if (Array.isArray(cur)) { for (const v of cur) stack.push(v); continue; }
    for (const [k, v] of Object.entries(cur)) {
      if (typeof v === 'string' && /[\d)(\-\s+]{6,}/.test(v)) {
        const n = normalizePhone(v);
        if (n) return { phone: n, source: `deep:${k}`, raw: v };
      }
      if (v && typeof v === 'object') stack.push(v);
    }
  }
  return { phone: '', source: null, raw: null };
}

// ---------- MAIN ----------
const items = await $input.all();
const out = [];

for (const item of items) {
  const input = item.json ?? item;
  const conf = input.alfacrm || {};

  const host = conf.host || input.host;
  const email = conf.email || input.email;
  const api_key = conf.api_key || input.api_key;
  if (!host || !email || !api_key) {
    throw new Error('Provide alfacrm.host, alfacrm.email, alfacrm.api_key');
  }

  const token = await login(this, { host, email, api_key });

  const lead_source_id =
    toNum(findKeyDeep(input, k => k === 'lead_source_id')) ??
    toNum(input.lead_source_id) ?? 3;

  const f = asObj(input.fields);
  const name  = f['1_2'] || input.name || 'Без имени';

  // умный телефон
  const { phone, source: phone_source, raw: phone_raw } = pickPhoneSmart(input);

  // на заметку — город (не используем 1_7, т.к. там часто телефон)
  const city = f['city'] || input.city || '';

  // UTM / yclid
  const utm_source   = input.utm_source   || '';
  const utm_medium   = input.utm_medium   || '';
  const utm_campaign = input.utm_campaign || '';
  const utm_content  = input.utm_content  || '';
  const utm_term     = input.utm_term     || '';
  const yclid        = input.yclid        || '';

  const custom_value_json = JSON.stringify({
    utm_source, utm_medium, utm_campaign, utm_content, utm_term, yclid,
  });

  const payload = {
    name,
    branch_ids: [1],
    legal_type: 1,
    is_study: 0,
    lead_source_id,
    ...(notEmpty(phone) ? { contact_contacts: [phone], phone: [phone] } : {}), // <-- ключевой дубль
    ...(city ? { note: `Город: ${city}` } : {}),
    ...(notEmpty(utm_source)   ? { custom_utm_source: utm_source } : {}),
    ...(notEmpty(utm_medium)   ? { custom_utm_medium: utm_medium } : {}),
    ...(notEmpty(utm_campaign) ? { custom_utm_campaign: utm_campaign } : {}),
    ...(notEmpty(utm_content)  ? { custom_utm_content: utm_content } : {}),
    ...(notEmpty(utm_term)     ? { custom_utm_term: utm_term } : {}),
    ...(notEmpty(yclid)        ? { custom_yclid: yclid } : {}),
    custom_value_json,
  };

  const res = await createCustomer(this, { host, token, payload });
  out.push({
    json: {
      success: res.success,
      response: res.response,
      sent: payload,
      debug_phone: { source: phone_source, raw: phone_raw, final: phone }
    }
  });
}

return out;

// n8n Code (JavaScript) — Run Once for All Items
const items = await $input.all();

// --- helpers ---
const notEmpty = v => v !== undefined && v !== null && String(v).trim() !== '';

function tryParseJSON(s) {
  if (typeof s !== 'string') return s;
  try { return JSON.parse(s); } catch { return s; }
}

function getQueryParam(url, name) {
  try { return new URL(String(url)).searchParams.get(name); } catch { return null; }
}

/**
 * Рекурсивно находит ВСЕ ключи "lead_source_id" и подменяет значение.
 * Сохраняет тип: если раньше было "40" (string), запишет "8"/"3" как строку.
 * Возвращает количество замен.
 */
function replaceLeadSourceId(obj, newValNumber) {
  let count = 0;
  const stack = [obj];
  const seen = new Set();

  while (stack.length) {
    const cur = stack.pop();
    if (!cur || typeof cur !== 'object') continue;
    if (seen.has(cur)) continue;
    seen.add(cur);

    if (Array.isArray(cur)) {
      for (const v of cur) if (v && typeof v === 'object') stack.push(v);
      continue;
    }

    for (const [k, v] of Object.entries(cur)) {
      if (k === 'lead_source_id') {
        const keepString = typeof v === 'string';
        cur[k] = keepString ? String(newValNumber) : newValNumber;
        count++;
      }
      if (v && typeof v === 'object') stack.push(v);
    }
  }
  return count;
}

for (const item of items) {
  const src = item.json ?? item;

  // --- где взять yclid ---
  let yclid = src.yclid ?? src.yclidid ?? null;

  // из page_url (?yclid=...)
  if (!notEmpty(yclid) && src.page_url) {
    const q = getQueryParam(src.page_url, 'yclid');
    if (notEmpty(q)) yclid = q;
  }

  // из custom_value_json (если там хранится)
  if (!notEmpty(yclid) && src.custom_value_json) {
    const cv = tryParseJSON(src.custom_value_json);
    if (cv && typeof cv === 'object' && 'yclid' in cv && notEmpty(cv.yclid)) {
      yclid = cv.yclid;
    }
  }

  const newVal = notEmpty(yclid) ? 8 : 3;

  // подменяем ВЕЗДЕ, где уже есть lead_source_id
  const replaced = replaceLeadSourceId(src, newVal);

  // лог для проверки
  console.log({ resolved_yclid: yclid ?? null, new_lead_source_id: newVal, replaced_keys: replaced });

  // если нужно также гарантированно обновить верхнеуровневое значение (которое ты показал в примере),
  // и оно есть в корне — продублируем:
  if ('lead_source_id' in src) {
    src.lead_source_id = typeof src.lead_source_id === 'string' ? String(newVal) : newVal;
  }

  // записываем обратно в item
  if (item.json) item.json = src; else Object.assign(item, src);
}

return items;

// Code (Function) node
// Вход: $json.body = строка с JSON
// Выход: нормальный объект, доступный далее как $json
const raw = $json.body || '';
let parsed = {};
try { parsed = JSON.parse(raw); } catch(e) {}
return [{ ...parsed, _raw: raw }];
