// Экранирование под Telegram MarkdownV2
function escapeMarkdownV2(s) {
  // список из оф. доки Telegram для MarkdownV2:
  // _ * [ ] ( ) ~ ` > # + - = | { } . !
  return String(s).replace(/([_*\[\]()~`>#+\-=|{}.!])/g, '\\$1');
}
// ФУНКЦИИ для «оставшегося времени»
function remainingSecondsNF(i) {
  // если NF нет — 0
  if (!i.nf_exists || i.execution_time_nf == null || i.steps_total_nf == null) return 0;

  const done = Number(i.steps_passed_nf || 0);
  const total = Number(i.steps_total_nf || 0);
  if (done <= 0 || total <= 0 || done >= total) return 0;

  const elapsedSec = toSeconds(i.execution_time_nf); // сколько уже отработал NF
  const avgPerStep = elapsedSec / done;
  const leftSteps = total - done;
  return Math.max(0, Math.round(avgPerStep * leftSteps));
}

function remainingSecondsBase(i) {
  // считаем ТОЛЬКО базовые шаги, исключая NF-шаг
  const nf = Number(i.nf_exists || 0);
  const passedBase = Math.max(0, Number(i.steps_passed || 0) - nf);
  const totalBase  = Math.max(0, Number(i.steps_total  || 0) - nf);
  const leftBase   = Math.max(0, totalBase - passedBase);

  if (leftBase === 0) return 0;                // нечего оставлять
  if (passedBase <= 0) return 0;               // нет базы для оценивания средней скорости

  const elapsedSec = toSeconds(i.execution_time || 0);
  const avgPerStep = elapsedSec / passedBase;  // средняя по БАЗЕ
  return Math.max(0, Math.round(avgPerStep * leftBase));
}
function formatSeconds(sec) {
  const totalMinutes = Math.floor(sec / 60);

  if (totalMinutes < 60) {
    return `${totalMinutes} мин`;
  } else {
    const h = Math.floor(totalMinutes / 60);
    const m = totalMinutes % 60;
    return m ? `${h} ч ${m} мин` : `${h} ч`;
  }
}
function toSeconds(exec) {
  const days = Number(exec.days || 0);
  const hours = Number(exec.hours || 0);
  const minutes = Number(exec.minutes || 0);
  const seconds = Number(exec.seconds || 0);
  const ms = Number(exec.milliseconds || 0);

  const totalMs =
    (((days * 24 + hours) * 60 + minutes) * 60 + seconds) * 1000 + ms;

  return Math.round(totalMs / 1000);
}

// Универсальный форматтер: "до часа — X мин", "больше часа — H ч M мин"
function formatDurationToHM(input) {
  const ms = toMilliseconds(input);
  const totalMinutes = Math.round(ms / 60000);

  if (totalMinutes < 60) {
    return `${totalMinutes} мин`;
  } else {
    const h = Math.floor(totalMinutes / 60);
    const m = totalMinutes % 60;
    return m ? `${h} ч ${m} мин` : `${h} ч`;
  }
}

// ---- Парсеры ----
function toMilliseconds(v) {
  if (v == null) throw new Error('No duration provided');

  // 1) Объект со свойствами
  if (typeof v === 'object' && !Array.isArray(v)) {
    const h = num(v.hours);
    const m = num(v.minutes);
    const s = num(v.seconds);
    const ms = num(v.milliseconds);
    return ((h*3600 + m*60 + s) * 1000) + ms;
  }

  // 2) Число: миллисекунды (если маленькое целое — трактуем как секунды)
  if (typeof v === 'number') {
    if (Number.isInteger(v) && Math.abs(v) < 1e6) return v * 1000; // секундные значения
    return v; // миллисекунды
  }

  // 3) Строки
  if (typeof v === 'string') {
    const s = v.trim();

    // 3.1 ISO 8601: PT#H#M#S
    const iso = /^P(T(?:(\d+)H)?(?:(\d+)M)?(?:(\d+(?:\.\d+)?)S)?)$/i.exec(s);
    if (iso) {
      const h = num(iso[1]); // в этой группе вся "T...", нам нужны 2..4 — поправим:
    }
    const iso2 = /^PT(?:(\d+)H)?(?:(\d+)M)?(?:(\d+(?:\.\d+)?)S)?$/i.exec(s);
    if (iso2) {
      const h = num(iso2[1]);
      const m = num(iso2[2]);
      const sec = num(iso2[3]);
      return ((h*3600 + m*60 + sec) * 1000);
    }

    // 3.2 HH:MM:SS(.sss) или MM:SS(.sss)
    const hms = /^(\d{1,2}):([0-5]?\d)(?::([0-5]?\d(?:\.\d+)?))?$/.exec(s);
    if (hms) {
      const a = hms[1], b = hms[2], c = hms[3];
      let h = 0, m = 0, sec = 0;
      if (c != null) { // HH:MM:SS
        h = num(a); m = num(b); sec = num(c);
      } else {         // MM:SS
        m = num(a); sec = num(b);
      }
      return ((h*3600 + m*60 + sec) * 1000);
    }

    // 3.3 Человеческие: "1h 3m 44.6s", "3m44s", "90s"
    const human = /^\s*(?:(\d+)\s*h)?\s*(?:(\d+)\s*m)?\s*(?:(\d+(?:\.\d+)?)\s*s)?\s*$/i.exec(s.replace(/,/g,'.'));
    if (human && (human[1] || human[2] || human[3])) {
      const h = num(human[1]);
      const m = num(human[2]);
      const sec = num(human[3]);
      return ((h*3600 + m*60 + sec) * 1000);
    }

    // 3.4 Чистое число в строке → сек/мс
    if (/^\d+(\.\d+)?$/.test(s)) {
      const val = Number(s);
      return (Number.isInteger(val) && val < 1e6) ? val * 1000 : val;
    }

    throw new Error('Unsupported duration format: ' + s);
  }

  throw new Error('Unsupported input type: ' + typeof v);
}

