async function http(req) { return await this.helpers.httpRequest(req); }

async function login(self, { host, email, api_key }) {
  const res = await http.call(self, {
    method: 'POST',
    url: `https://${host}/v2api/auth/login`,
    headers: { 'Content-Type': 'application/json' },
    body: { email, api_key },
    json: true,
  });
  if (!res?.token) throw new Error('Auth failed');
  return res.token;
}

async function createCustomer(self, { host, token, payload }) {
  try {
    const res = await http.call(self, {
      method: 'POST',
      url: `https://${host}/v2api/1/customer/create`,
      headers: {
        'Content-Type': 'application/json',
        'X-ALFACRM-TOKEN': token,
      },
      body: payload,
      json: true,
      gzip: true,
      timeout: 20000,
    });
    return { success: true, response: res };
  } catch (err) {
    return {
      success: false,
      status: err?.response?.statusCode,
      error: err?.response?.body || err.message,
    };
  }
}

// ---------- helpers ----------
const notEmpty = v => v !== undefined && v !== null && String(v).trim() !== '';
const toNum = v => {
  if (!notEmpty(v)) return undefined;
  const n = Number(String(v).trim());
  return Number.isFinite(n) ? n : undefined;
};
function firstNonEmpty(arr) {
  for (const v of arr) if (notEmpty(v)) return String(v).trim();
  return '';
}

// нормализация телефона
function normalizePhone(raw) {
  const s = String(raw || '').trim();
  if (!s) return '';
  let keep = s.replace(/[^\d+]/g, '');
  if (keep.startsWith('00')) keep = '+' + keep.slice(2);
  const digitsOnly = keep.replace(/\D/g, '');
  if (digitsOnly.length === 11 && keep[0] === '8') keep = '+7' + digitsOnly.slice(1);
  if (keep[0] !== '+') keep = '+' + digitsOnly;
  const finalDigits = keep.replace(/\D/g, '');
  if (finalDigits.length < 8 || finalDigits.length > 15) return '';
  return keep;
}

// форматируем dob к ISO (YYYY-MM-DD)
function formatDob(raw) {
  if (!notEmpty(raw)) return '';
  let s = String(raw).trim();

  // уже ISO?
  if (/^\d{4}-\d{2}-\d{2}$/.test(s)) return s;

  // формат DD.MM.YYYY → YYYY-MM-DD
  if (/^\d{2}\.\d{2}\.\d{4}$/.test(s)) {
    const [d, m, y] = s.split('.');
    return `${y}-${m}-${d}`;
  }

  // формат YYYY/MM/DD → YYYY-MM-DD
  if (/^\d{4}\/\d{2}\/\d{2}$/.test(s)) {
    return s.replace(/\//g, '-');
  }

  // универсальный fallback
  const date = new Date(s);
  if (isNaN(date)) return '';
  return date.toISOString().slice(0, 10);
}

function buildNote({ message, city }) {
  const lines = [];
  if (notEmpty(message)) lines.push(`Сообщение: ${message}`);
  if (notEmpty(city))     lines.push(`Город: ${city}`);
  return lines.join('\n');
}

// ---------- MAIN ----------
const items = await $input.all();
const out = [];

for (const item of items) {
  const input = item.json ?? item;
  const conf = input.alfacrm || {};

  const host    = conf.host  || input.host;
  const apiMail = conf.email || input.email;
  const api_key = conf.api_key || input.api_key;
  if (!host || !apiMail || !api_key) {
    throw new Error('Provide alfacrm.host, alfacrm.email, alfacrm.api_key');
  }

  const token = await login(this, { host, email: apiMail, api_key });
  const lead_source_id = toNum(input.lead_source_id) ?? 3;

  const src = input.body ?? input;

  const childName  = firstNonEmpty([ src.child_name, src['child name'] ]) || 'Без имени';
  const parentName = firstNonEmpty([ src.parent_name ]);

  // преобразуем dob к ISO
  const dobRaw = firstNonEmpty([ src.child_birthdate ]);
  const dob = formatDob(dobRaw);

  const phoneRaw   = firstNonEmpty([ src.phone ]);
  const phone      = normalizePhone(phoneRaw);
  const emailForm  = firstNonEmpty([ src.email ]);

  const phoneArr = [phone].filter(notEmpty);
  const emailArr = [emailForm].filter(notEmpty);

  const message = firstNonEmpty([ src.message ]);
  const city    = firstNonEmpty([ src.city ]);
  const note    = buildNote({ message, city });

  const utm_source   = src.utm_source   || '';
  const utm_medium   = src.utm_medium   || '';
  const utm_campaign = src.utm_campaign || '';
  const utm_content  = src.utm_content  || '';
  const utm_term     = src.utm_term     || '';
  const yclid        = src.yclid        || '';

  const custom_value_json = JSON.stringify({
    utm_source, utm_medium, utm_campaign, utm_content, utm_term, yclid,
  });

  const payload = {
    name: childName,
    ...(notEmpty(parentName) ? { legal_name: parentName } : {}),
    ...(notEmpty(dob) ? { dob } : {}),
    branch_ids: [1],
    legal_type: 1,
    is_study: 0,
    lead_source_id,
    ...(phoneArr.length ? { phone: phoneArr } : {}),
    ...(emailArr.length ? { email: emailArr } : {}),
    ...(notEmpty(note) ? { note } : {}),
    ...(notEmpty(utm_source)   ? { custom_utm_source: utm_source } : {}),
    ...(notEmpty(utm_medium)   ? { custom_utm_medium: utm_medium } : {}),
    ...(notEmpty(utm_campaign) ? { custom_utm_campaign: utm_campaign } : {}),
    ...(notEmpty(utm_content)  ? { custom_utm_content: utm_content } : {}),
    ...(notEmpty(utm_term)     ? { custom_utm_term: utm_term } : {}),
    ...(notEmpty(yclid)        ? { custom_yclid: yclid } : {}),
    custom_value_json,
  };

  const res = await createCustomer(this, { host, token, payload });
  out.push({
    json: {
      success: res.success,
      response: res.response,
      sent: payload,
    }
  });
}

return out;
